public void updateBot(){

    steamBody.setPosition((body.getPosition().x - 1.0f) * GameInfo.PPM,
            (body.getPosition().y - 1.0f) * GameInfo.PPM);

    // Call set face to determine which face to display
    setFace();

}

// setFace determines which face and calls relevant function below
private void setFace(){
        if (body.getLinearVelocity().y > 1.0f){
            upOrient();
        } else if (body.getLinearVelocity().y < -1.0f){
            downOrient();
        } else {
            neutralOrient();
        }
    }

    public void upOrient(){
        steamLeftEye.setPosition(steamBody.getX() + 45.0f, steamBody.getY() + 140.0f);
        steamRightEye.setPosition(steamBody.getX() + (steamBody.getWidth() / 2) + 5.0f, steamBody.getY() + 140.0f);
        steamLeftEar.setPosition(steamBody.getX() + 15.0f, steamBody.getY() + 60.0f);
        steamRightEar.setPosition(steamBody.getX() + (steamBody.getWidth() / 2) + 30.0f, steamBody.getY() + 60.0f);
    }
    public void neutralOrient(){
        steamLeftEye.setPosition(steamBody.getX() + 45.0f, steamBody.getY() + 100.0f);
        steamRightEye.setPosition(steamBody.getX() + (steamBody.getWidth() / 2) + 5.0f, steamBody.getY() + 100.0f);
        steamLeftEar.setPosition(steamBody.getX() + 15.0f, steamBody.getY() + 100.0f);
        steamRightEar.setPosition(steamBody.getX() + (steamBody.getWidth() / 2) + 30.0f, steamBody.getY() + 100.0f);
    }
    public void downOrient(){
        steamLeftEye.setPosition(steamBody.getX() + 45.0f, steamBody.getY() + 40.0f);
        steamRightEye.setPosition(steamBody.getX() + (steamBody.getWidth() / 2) + 5.0f, steamBody.getY() + 40.0f);
        steamLeftEar.setPosition(steamBody.getX() + 15.0f, steamBody.getY() + 140.0f);
        steamRightEar.setPosition(steamBody.getX() + (steamBody.getWidth() / 2) + 30.0f, steamBody.getY() + 140.0f);

    }
