public void drawBot(SpriteBatch batch){

    botElapsedTime += Gdx.graphics.getDeltaTime();

    if (powerOn){
        batch.draw(botSteamAnimation.getKeyFrame(botElapsedTime, true),
                steamBody.getX(), steamBody.getY() - 200.0f);
    }

    batch.draw(steamValve,steamValve.getX(),steamValve.getY());

    if (onBurner || onPortableBurner){ // Animated rings when on initial burner or any portable burner
        glowRing(batch);
    }

    batch.draw(steamLeftEar, steamLeftEar.getX(),steamLeftEar.getY());
    batch.draw(steamRightEar, steamRightEar.getX(), steamRightEar.getY());
    batch.draw(steamBody,steamBody.getX(),steamBody.getY());
    batch.draw(steamLeftEye,steamLeftEye.getX(),steamRightEye.getY());
    batch.draw(steamRightEye,steamRightEye.getX(),steamRightEye.getY());
}

if (onBurner || onPortableBurner){ // Animated rings when on initial burner or any portable burner
        glowRing(batch);
}

if (targetObstacle.distanceFromBot.y > 200.0f){
    steamBot.onPortableBurner = false;
    isOnPortableBurner = false;
}
if (targetObstacle.distanceFromBot.y < 60.0f) {
    steamBot.burnerBounce();
}
