private void buildSteam(float delta) {

        if (gm.waterLevel < 1.0f && gm.steamLevel > 0) {

            gm.steamLevel -= GameInfo.STEAM_SCALE * delta;
            gm.waterLevel += GameInfo.WATER_SCALE * delta;

