cells.data:
li a {
        display: block;
        color: white;
        padding: 8px 16px;
        text-decoration: none;
        text-align: center;

        transition: background-color .5s ease-in, transform .5s .25s ease-in-out;
    }

    li a:hover { 
        background-color: tomato;
        transform: scale(1.2);
    }
}
