}

ul {
    list-style-type: none;
    margin: 0;
    padding: 0;
    width: 100%;
    height: 35px;
    background-color: $darkpurple;
    li {
        float: left;
    }
    li a {
        display: block;
        padding: 8px 16px;
        color: white;
        text-decoration: none;
        text-align: center;
    }
}

